    }

    return ( 
        <div>
            <button onClick={handleGetPlaylists}>Get Playlists</button>
            <button onClick={handleCreatePlaylists}>Create Playlists</button>
            <button onClick={handleAddItem}>Add Item</button>
        </div>
     );
}
 
export default SpotifyGetPlaylists;
