import NextButton from "./NextButton";
import CheckBox from "./CheckBox";
import { Link } from 'react-router-dom';
import { useState } from "react";

const optionTwo = ['Male', 'Female', 'Other (please specify):' ]
const optionThree = ['African American/Black','Asian','Caucasian/White','Hispanic/Latino','Middle Eastern', 'Native American/Indigenous','Pacific Islander', 'Mixed Race','Other (please specify):']


const QuestionOne = () => {

    // This is for CheckBox
    const [selected, setSelected] = useState(null);
    const [hidden, setHidden] = useState("hidden");
    const [disabled, setDisabled] = useState("disabled");

    const onChange = (optionID,optionArray) => {
        setSelected((optionID))
        // console.log("please",{optionArray.le})


        if(optionID === optionArray.length-1 && optionArray[optionID] === "Other (please specify):"){
            setHidden("");
            setDisabled("");
        }else{
            setHidden("hidden");
            setDisabled("disabled")
        }
        
    }
    
    const [optionOne,setOptionOne] = useState('')

    const handleSubmit = async (e) => {
        e.preventDefault()

        const workout = {optionOne}
    }

    const [check,setCheck] = useState("false");

    const checkInput = (inputValue) => {
        
        if(!inputValue.target.value.trim() ){
            //setCheck = "false"
            setCheck("false");
            console.log({check})
        }

        if(inputValue.target.value.trim() ){
            //setCheck = "false"
            setCheck("true");
            console.log({check})
        }

        
    }

    // This is the HTML part
    return ( 
    <form onSubmit={handleSubmit}>
        <div>
            <label>1. Age: </label>
            <input type = "number" />
            
        </div>
        <div>
            <label>2. Gender:</label>
            <div>
                {optionTwo.map((option,optionID) => (
                    <div key = {optionID}>
                            {<input 
                                type="checkbox"
                                checked={optionID === selected}
                                onChange={() => onChange(optionID,optionTwo)}
                            />}
                        
                            <label>{option}</label>
                                        
                    </div>

                ))}
            </div>
        </div>
        <div>
            <label>3. Ethnicity:</label>
            <div>
                {optionThree.map((option,optionID) => (
                    <div key = {optionID}>
                            {<input 
                                type="checkbox"
                                checked={optionID === selected}
                                onChange={() => onChange(optionID,optionThree)}
                            />}
                        
                            <label>{option}</label>
                                        
                    </div>

                ))}
            </div>
        </div>
        <div>
            <label>4. Primary Language you speck: </label>
            <input type = "text" />
        </div>
        <div>
            <label>5. Country you live: </label>
            <input type = "text" />
        </div>
        <Link to="/questiontwo"><button>Next</button></Link>
    </form>
     );
}
 
export default QuestionOne;
