import axios from "axios"
import { useState, useEffect } from "react";

const PLAYLIST_ENDPOINT = "https://api.spotify.com/v1/me/player/currently-playing"

const SpotifyGetPlaylists = () => {

    const [token,setToken] = useState("")
    const [data, setData] = useState({})

    useEffect(() => {
             
        if(localStorage.accessToken){
            setToken(localStorage.accessToken)
        }

    },[])
     
    
    const handleGetPlaylists = async () => {

        await new Promise(() => {
            axios
                .get(PLAYLIST_ENDPOINT,{
                    headers:{
                        Authorization: "Bearer " + token,
                    },
                    
                })
                .then((response) => {
                    setData(response.data)
                    console.log("Track name:",response.data.item.name)
                    console.log("Timestamp:",response.data.timestamp)
                    console.log("Token from set",response)
                    console.log("Token from set",data)
                })
                .catch((error) => {
                    console.log(error)
                })
            })        

        
    }

    return ( 
        <div>
            <button onClick={handleGetPlaylists}>Get Playlists</button>

        </div>
     );
}
 
export default SpotifyGetPlaylists;
